import { useSelector } from 'react-redux'


const Front = () => {

    const states = useSelector(reducer => reducer.clockState)

    let dayToMili = 86400000
    let hourToMili = 3600000
    let minuteToMili = 60000
    let secondToMili = 1000

    //MONTH IS 0 INDEXED WHY????

    let remainingtime_s = states.time - new Date(2021, 4, 25, 2, 0, 0)
    let days = Math.floor(remainingtime_s/dayToMili)
    let remainingtime = remainingtime_s%dayToMili
    let hours = Math.floor(remainingtime/hourToMili)
    remainingtime = remainingtime%hourToMili
    let minutes = Math.floor(remainingtime/minuteToMili)
    remainingtime = remainingtime%minuteToMili
    let seconds = Math.floor(remainingtime/secondToMili)


    return (
        <div className='App-front'>
            {/* <p>{new Date(states.time - new Date(2021, 5, 25, 2, 0, 0)).toLocaleDateString("en-US", { weekday: 'long', year: 'numeric', month: 'long', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit'})}</p> */}
            <p>{remainingtime_s} : {days} : {hours} : {minutes} : {seconds}</p>

            <p>{states.time.toLocaleDateString("en-US", { weekday: 'long', year: 'numeric', month: 'long', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit'})}</p>
        </div>
    )
}

export default Front
