import { useSelector } from 'react-redux'


const Front = () => {

    const states = useSelector(reducer => reducer.clockState)

    return (
        <div className='App-front'>
            {/* <p>{new Date(states.time - new Date(2021, 5, 25, 2, 0, 0)).toLocaleDateString("en-US", { weekday: 'long', year: 'numeric', month: 'long', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit'})}</p> */}
            <p>{new Date(Math.abs(states.time - new Date(2021, 4, 25, 13, 20, 0))).toLocaleDateString("en-US", {hour: '2-digit', minute: '2-digit', second: '2-digit'})}</p>

            <p>{states.time.toLocaleDateString("en-US", { weekday: 'long', year: 'numeric', month: 'long', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit'})}</p>
        </div>
    )
}

export default Front
