const initalState = {
    loggedIn: false,
    toggle: false
}

const loginReducer = (state = initalState, action) => {
    switch (action.type) {
        case 'GET_LOGIN':
            return {
                ...state,
                loggedIn: true
            }
        case 'TOGGLE':
            return {
                ...state,
                toggle: !state.toggle
            }
        default:
            return state
    }
}

export default loginReducer
