const tick = ({ interval }) => {
    return {
        type: 'TICK',
        time: format(new Date(), 'h:m:ss:SSS'),
        interval
    }
}


export const startTimer = (dispatch) => {

    dispatch({
        type: 'START_TIMER'
    });

    clearInterval(getState.interval);

    const interval = setInterval(() => {
        dispatch(tick({ interval }))
    }, 10)
}
