// import stopwatch from "../images/stopwatch.jpg"
import { useSelector, useDispatch } from 'react-redux'
import { fetchLogin } from '../actions/loginAction'

const Front = () => {
    const states = useSelector(reducer => reducer.loginState)
    const dispatch = useDispatch()
    return (
        <div className='App-front'>
            {states.fool ? <h1 className="fool">YOU FOOL. YOU ABSOLUTE BAFFOON HOW COULD YOU DO THAT!</h1> : <h1></h1>}
            {states.toggle ? <h1>Sign Up</h1> : <h1>Sign In</h1>}
            <form onSubmit={(e) => { e.preventDefault(); fetchLogin(states.toggle, e, dispatch) }} >
                <label>Username: </label>
                <input required type="text" />
                <br />
                <label>Password: </label>
                <input required type="text" />
                <br />
                <input type="submit" />
            </form>
            <br></br>
            <button onClick={() => dispatch({ type: 'TOGGLE' })}>{states.toggle ? "Sign in" : "Sign up"}</button>

        </div>
    )
}

export default Front
