const initalState = {
    loggedIn: false,
    toggle:false
}

const loginReducer = (state = initalState, action) => {
    switch(action.type){
        case 'GET_LOGIN':
            return{
                ...state,
                loggedIn: true
            }
        default:
            return state
    }
}

export default loginReducer
