export const fetchLogin = (toggle, e, dispatch) => {

    if (toggle === false) {

        let return_action = {}

        let user = {
            name: e.target[0].value,
            password: e.target[1].value,
        };

        let reqPackage = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json",
            },
            body: JSON.stringify(user),
        };

        fetch("http://localhost:3000/api/v1/login", reqPackage)
            .then((res) => res.json())
            .then((data) => {
                data.token === "Incorrect name or password" ?
                    dispatch({ type: 'FOOL' }) :
                    localStorage.setItem("token", data.token)
                dispatch({ type: 'GET_LOGIN', username: data.name });
            })

    }

    if (toggle === true) {

        let return_action = {}

        let user = {
            name: e.target[0].value,
            password: e.target[1].value,
        };

        let reqPackage = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json",
            },
            body: JSON.stringify(user),
        };


        // adjust front and back end for below so that it makes a new user

        fetch("http://localhost:3000/api/v1/signup", reqPackage)
            .then((res) => res.json())
            .then((data) => {
                data.token === "Incorrect name or password" ?
                    dispatch({ type: 'FOOL' }) :
                    localStorage.setItem("token", data.token)
                dispatch({ type: 'SIGNUP', username: data.name });
            })

    }
}
