export const fetchLogin = (toggle, e) => {
    console.log(toggle, e.target[0].value, e.target[1].value)

    if (toggle === false) {

        let return_action = {}

        let user = {
            name: e.target[0].value,
            password: e.target[1].value,
        };

        let reqPackage = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json",
            },
            body: JSON.stringify(user),
        };

        fetch("http://localhost:3000/api/v1/login", reqPackage)
            .then((res) => res.json())
            .then((data) => {
                data.token === "Incorrect name or password" ?
                    return_action = { type: 'FOOL'} :
                    localStorage.setItem("token", data.token)
                    return_action = { type: 'GET_LOGIN' , username: data.name};

            })

        console.log(return_action)

        // return (return_action)

    }
}
