const initalState = {
    pizzas:[]
}

const pizzaReducer = (state= initalState, action) => {
    switch(action.type){
        case 'GET_PIZZAS':
            return{
                ...state,
                pizzas: action.pizzas
            }
        default:
            return state
    }
}

export default pizzaReducer
