const initalState = {
    loggedIn: false,
    toggle: false,
    fool: false,
    username: ""
}

const loginReducer = (state = initalState, action) => {
    switch (action.type) {
        case 'GET_LOGIN':
            return {
                ...state,
                loggedIn: true,
                username: action.username
            }
        case 'FOOL':
            return {
                ...state,
                fool: true
            }
        case 'TOGGLE':
            return {
                ...state,
                toggle: !state.toggle
            }
        default:
            return state
    }
}

export default loginReducer
