// import stopwatch from "../images/stopwatch.jpg"
import { useSelector, useDispatch } from 'react-redux'
import { fetchLogin } from '../actions/loginAction'

const Front = () => {
    const toggle = useSelector(reducer => reducer.loginState.toggle)
    const loggedIn = useSelector(reducer => reducer.loginState.loggedIn)
    const dispatch = useDispatch()
    return (
        <div className='App-front'>
            {toggle ? <h1>Sign up</h1> : <h1>Sign in</h1>}
            <form onSubmit= {(e) => {e.preventDefault(); fetchLogin(toggle, e)}} >
                <label>Username: </label>
                <input required type="text" />
                <br />
                <label>Password: </label>
                <input required type="text" />
                <br />
                <input type="submit"/>
            </form>
            <br></br>
            <button onClick = {() => dispatch({type:'TOGGLE'})}>{toggle ? "Sign in" : "Sign up"}</button>

        </div>
    )
}

export default Front
