import { useSelector } from 'react-redux'


const Front = () => {

    const states = useSelector(reducer => reducer.clockState)

    return (
        <div className='App-front'>
            <p>{states.time.getHours()}:{states.time.getMinutes()}:{states.time.getSeconds()}</p>
        </div>
    )
}

export default Front
