const tick = ({ interval }) => {
    return {
        type: 'TICK',
        time: new Date().toLocaleString('en-US', { timeZone: 'America/Mexico_City' }),
        interval
    }
}


export const startTimer = (dispatch) => {

    dispatch({
        type: 'TICK'
    });

    //clearInterval(getState.interval);

    const interval = setInterval(() => {
        dispatch(tick({ interval }))
    }, 1000)

}
